i := 1;
trovato := FALSE;
pos := -1;

while (not(trovato) AND (i<=max_dim)) do
        if (magazzino[i]=False) then
            pos := i;
            trovato := TRUE;
        else
            i := i+1;
        end_if;
end_while;

magazzino[pos]:=True;
counter := pos;

if( (manual_position<1) OR (manual_position>max_dim) ) then
    position:=0;
    errore_0:=1;

else
    if ( (magazzino[manual_position]=TRUE)) then
        position:=0;
        errore_0:=1;
    else
        position:=manual_position;
        magazzino[manual_position]:=TRUE;
        errore_0:=0;
     end_if;
end_if;

if (start_button=true) then
    ok_retrieve:=false;
end_if;

if ( (start_button=true) and (cont_mag=max_dim) and not(forw_button) ) then
    ok_retrieve:=true;
end_if;

if (start_button=false) then
    ok_retrieve:=true;
end_if;

if ((cont_mag=max_dim) and (forw_button=false)) then
    ok_ins:=false;
else
    ok_ins:=true;
end_if;

if( (manual_position<1) OR (manual_position>max_dim) ) then
    ok_ret_pos:=False;
    errore_0:=1;

else
    if ( (magazzino[manual_position]=False)) then
        ok_ret_pos:=False;
        errore_0:=1;
    else
        position:=manual_position;
        magazzino[manual_position]:=False;
        ok_ret_pos:=True;
        errore_0:=0;
     end_if;
end_if;
